// Dosya: client/src/pages/LoginPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Hook'u import et
import axios from 'axios'; // Axios'u import et
import './LoginPage.css';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // Hata mesajları için state
  const navigate = useNavigate(); // Yönlendirme için hook'u kullan

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(''); // Her denemede hatayı sıfırla

    try {
      const response = await axios.post('http://localhost:5001/api/auth/login', {
        username,
        password,
      });

      // Başarılı olursa token'ı al ve sakla
      const { token } = response.data;
      localStorage.setItem('authToken', token); // Token'ı tarayıcı hafızasında sakla

      // Başarılı giriş sonrası dashboard'a yönlendir
      // Sayfayı tamamen yenileyerek yönlendiriyoruz ki App.js'deki kontrol çalışsın
      window.location.href = '/dashboard'; 

    } catch (err) {
      // Hata olursa mesajı göster
      setError('Kullanıcı adı veya şifre hatalı!');
      console.error('Giriş hatası:', err.response?.data?.message || err.message);
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Esbo Admin Panel</h2>
        {/* Hata mesajını burada göster */}
        {error && <p className="error-message">{error}</p>}
        <div className="input-group">
          <label htmlFor="username">Kullanıcı Adı</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Şifre</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="login-button">Giriş Yap</button>
      </form>
    </div>
  );
};

export default LoginPage;
