// Dosya: client/src/components/layout/Navbar.js
import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
import './Navbar.css';

const Navbar = () => {
  const [username, setUsername] = useState('');
  const [dropdownOpen, setDropdownOpen] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      const decodedToken = jwtDecode(token);
      setUsername(decodedToken.username);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    window.location.href = '/login';
  };

  return (
    <header className="navbar">
      <div className="navbar-left">
        <span>Hoşgeldin, <strong>{username}</strong>!</span>
      </div>
      <div className="navbar-right">
        <div className="user-menu" onClick={() => setDropdownOpen(!dropdownOpen)}>
          <span>{username}</span>
          <div className={`dropdown-menu ${dropdownOpen ? 'show' : ''}`}>
            <a href="#profile" onClick={(e) => { e.preventDefault(); alert('Şifre güncelleme yakında!'); }}>Şifre Güncelle</a>
            <a href="#logout" onClick={handleLogout}>Çıkış Yap</a>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
