// Dosya: client/src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import AdminLayout from './components/layout/AdminLayout';
import DashboardPage from './pages/admin/DashboardPage';

function App() {
  // Tarayıcı hafızasında token var mı diye kontrol et
  const token = localStorage.getItem('authToken');
  const isAuthenticated = token ? true : false; 

  return (
    <BrowserRouter>
      <Routes>
        {/* Eğer kullanıcı giriş yapmışsa ve /login'e gitmeye çalışırsa dashboard'a yönlendir */}
        <Route 
          path="/login" 
          element={!isAuthenticated ? <LoginPage /> : <Navigate to="/dashboard" />} 
        />

        <Route 
          path="/*"
          element={
            isAuthenticated ? <AdminLayout /> : <Navigate to="/login" />
          }
        >
            <Route path="dashboard" element={<DashboardPage />} />
            {/* Ana sayfa için de bir yönlendirme ekleyelim */}
            <Route index element={<Navigate to="/dashboard" />} />
        </Route>

      </Routes>
    </BrowserRouter>
  );
}

export default App;
