// Dosya: client/src/contexts/SocketContext.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import io from 'socket.io-client';

const SocketContext = createContext();

export const useSocket = () => {
    return useContext(SocketContext);
};

export const SocketProvider = ({ children }) => {
    const [socket, setSocket] = useState(null);

    // YENİ: Bildirim sesini çalacak olan merkezi fonksiyon
    const playNotificationSound = () => {
        try {
            // Ayarları tarayıcının yerel hafızasından (localStorage) okuyoruz
            const userSettings = JSON.parse(localStorage.getItem('userSettings'));

            // Eğer kullanıcı ayarları varsa, sesler açıksa ve bir ses seçilmişse...
            if (userSettings && userSettings.soundEnabled && userSettings.selectedSound) {
                // Yeni bir ses nesnesi oluşturup, sunucudaki ses dosyasının tam adresini veriyoruz
                const sound = new Audio(`http://localhost:5001${userSettings.selectedSound}`);
                sound.play();
            }
        } catch (error) {
            console.error("Bildirim sesi çalınırken hata:", error);
        }
    };

    useEffect(() => {
        const newSocket = io('http://localhost:5001');
        setSocket(newSocket);

        // YENİ: Yeni talep sinyallerini dinle ve ses çalma fonksiyonunu tetikle
        newSocket.on('new_investment_request', playNotificationSound);
        newSocket.on('new_withdrawal_request', playNotificationSound);

        // Sayfa kapandığında dinleyicileri ve bağlantıyı temizle
        return () => {
            newSocket.off('new_investment_request', playNotificationSound);
            newSocket.off('new_withdrawal_request', playNotificationSound);
            newSocket.close();
        };
    }, []); // Boş dependency array sayesinde sadece bir kez çalışır

    return (
        <SocketContext.Provider value={socket}>
            {children}
        </SocketContext.Provider>
    );
};
