// Dosya: client/src/App.js
import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import AdminLayout from './components/layout/AdminLayout';
import DashboardPage from './pages/admin/DashboardPage';

function App() {
  // Şimdilik basit bir giriş kontrolü yapıyoruz.
  // Gerçek kimlik doğrulama sonraki adımlarda eklenecek.
  const isAuthenticated = false; 

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/login" element={<LoginPage />} />

        {/* Admin sayfaları için ana yol */}
        <Route 
          path="/*"
          element={
            isAuthenticated ? <AdminLayout /> : <Navigate to="/login" />
          }
        >
            {/* AdminLayout içinde gösterilecek alt sayfalar */}
            <Route path="dashboard" element={<DashboardPage />} />
            {/* Diğer admin sayfaları buraya eklenecek */}
        </Route>

      </Routes>
    </BrowserRouter>
  );
}

export default App;
