import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SettingsModal.css';

// Sistemde yerleşik olarak gelen ses seçenekleri
const defaultSounds = [
    { name: 'Varsayılan', url: '/sounds/default.mp3' },
    { name: 'Zil Sesi', url: '/sounds/bell.mp3' },
    { name: 'Melodi', url: '/sounds/melody.mp3' },
];

const SettingsModal = ({ userId, onClose }) => {
    const [settings, setSettings] = useState({ soundEnabled: true, selectedSound: '' });
    const [customSoundFile, setCustomSoundFile] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    // Pencere açıldığında, kullanıcının mevcut ayarlarını tarayıcı hafızasından yükle
    useEffect(() => {
        const currentSettings = JSON.parse(localStorage.getItem('userSettings'));
        if (currentSettings) {
            setSettings(currentSettings);
        }
    }, []);

    // "Sesli Bildirimler" anahtarını açıp kapatan fonksiyon
    const handleToggle = () => {
        setSettings(prev => ({ ...prev, soundEnabled: !prev.soundEnabled }));
    };

    // Ses seçim menüsünü yöneten fonksiyon
    const handleSoundChange = (e) => {
        setSettings(prev => ({ ...prev, selectedSound: e.target.value }));
    };

    // Kullanıcı yeni bir ses dosyası seçtiğinde onu state'e kaydeden fonksiyon
    const handleFileChange = (e) => {
        setCustomSoundFile(e.target.files[0]);
    };

    // "Kaydet" butonuna basıldığında çalışan ana fonksiyon
    const handleSave = async () => {
        setIsLoading(true);
        let finalSettings = { ...settings };

        // 1. Eğer yeni bir ses dosyası seçildiyse, önce onu sunucuya yükle
        if (customSoundFile) {
            const formData = new FormData();
            formData.append('soundFile', customSoundFile);
            try {
                const response = await axios.post(`http://localhost:5001/api/users/upload-sound/${userId}`, formData);
                finalSettings.selectedSound = response.data.soundUrl; // Sunucudan gelen yeni sesin URL'sini al
            } catch (error) {
                alert("Ses dosyası yüklenirken hata oluştu.");
                setIsLoading(false);
                return;
            }
        }

        // 2. Son ayarları (yeni ses URL'si dahil) sunucuya kaydet
        try {
            const response = await axios.put(`http://localhost:5001/api/users/${userId}/settings`, { settings: finalSettings });
            // 3. Tarayıcı hafızasındaki ayarları da anında güncelle
            localStorage.setItem('userSettings', JSON.stringify(response.data.settings));
            alert("Ayarlar kaydedildi!");
            onClose(); // Pencereyi kapat
        } catch (error) {
            alert("Ayarlar kaydedilirken hata oluştu.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>Bildirim Ayarları</h3>

                <div className="setting-row">
                    <label>Sesli Bildirimler</label>
                    <label className="switch">
                        <input type="checkbox" checked={settings.soundEnabled} onChange={handleToggle} />
                        <span className="slider round"></span>
                    </label>
                </div>

                <div className="setting-row">
                    <label htmlFor="sound-select">Bildirim Sesi</label>
                    <select id="sound-select" value={settings.selectedSound} onChange={handleSoundChange} disabled={!settings.soundEnabled}>
                        {defaultSounds.map(sound => (
                            <option key={sound.url} value={sound.url}>{sound.name}</option>
                        ))}
                    </select>
                </div>

                <div className="setting-row">
                    <label htmlFor="custom-sound">Özel Ses Yükle (MP3)</label>
                    <input type="file" id="custom-sound" accept=".mp3" onChange={handleFileChange} disabled={!settings.soundEnabled} />
                </div>

                <div className="form-actions">
                    <button onClick={handleSave} className="save-btn" disabled={isLoading}>
                        {isLoading ? 'Kaydediliyor...' : 'Kaydet'}
                    </button>
                    <button onClick={onClose} className="cancel-btn" disabled={isLoading}>Kapat</button>
                </div>
            </div>
        </div>
    );
};

export default SettingsModal;
