import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
import { useSocket } from '../../contexts/SocketContext';
import SettingsModal from '../common/SettingsModal';
import PasswordModal from '../common/PasswordModal';
import './Navbar.css';

const Navbar = () => {
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState(null);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [activeAdmins, setActiveAdmins] = useState([]);
  const socket = useSocket();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        setUsername(decodedToken.username);
        setUserId(decodedToken.id);
      } catch (error) {
        console.error("Geçersiz token:", error);
        handleLogout();
      }
    }
  }, []);

  useEffect(() => {
    if (socket == null) return;

    socket.on('update_active_admins', (admins) => {
      setActiveAdmins(admins);
    });

    return () => {
      socket.off('update_active_admins');
    };
  }, [socket]);


  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userSettings');
    window.location.href = '/login';
  };

  return (
    <>
      <header className="navbar">
        <div className="navbar-left">
          <div className="active-admins">
            <span className="active-dot"></span>
            {/* GÜNCELLEME: Sunucudan gelen temizlenmiş listenin kullanıcı adlarını göster */}
            <span>Aktif Adminler: {activeAdmins.map(admin => admin.username).join(', ')}</span>
          </div>
        </div>
        <div className="navbar-right">
          <div className="user-menu" onClick={() => setDropdownOpen(!dropdownOpen)}>
            <span>{username}</span>
            <div className={`dropdown-menu ${dropdownOpen ? 'show' : ''}`}>
              <a 
                href="#settings" 
                onClick={(e) => { e.preventDefault(); setShowSettingsModal(true); setDropdownOpen(false); }}
              >
                Ayarlar
              </a>
              <a 
                href="#password" 
                onClick={(e) => { e.preventDefault(); setShowPasswordModal(true); setDropdownOpen(false); }}
              >
                Şifre Güncelle
              </a>
              <a href="#logout" onClick={handleLogout}>Çıkış Yap</a>
            </div>
          </div>
        </div>
      </header>

      {showSettingsModal && (
        <SettingsModal userId={userId} onClose={() => setShowSettingsModal(false)} />
      )}

      {showPasswordModal && (
        <PasswordModal userId={userId} onClose={() => setShowPasswordModal(false)} />
      )}
    </>
  );
};

export default Navbar;
