import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
import SettingsModal from '../common/SettingsModal';
import PasswordModal from '../common/PasswordModal'; // YENİ: Şifre penceresini import et
import './Navbar.css';

const Navbar = () => {
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState(null);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showPasswordModal, setShowPasswordModal] = useState(false); // YENİ: Modal'ın görünürlüğünü kontrol eden state

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        setUsername(decodedToken.username);
        setUserId(decodedToken.id);
      } catch (error) {
        console.error("Geçersiz token:", error);
        handleLogout();
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userSettings');
    window.location.href = '/login';
  };

  return (
    <>
      <header className="navbar">
        <div className="navbar-left">
          <span>Hoşgeldin, <strong>{username}</strong>!</span>
        </div>
        <div className="navbar-right">
          <div className="user-menu" onClick={() => setDropdownOpen(!dropdownOpen)}>
            <span>{username}</span>
            <div className={`dropdown-menu ${dropdownOpen ? 'show' : ''}`}>
              <a 
                href="#settings" 
                onClick={(e) => { 
                  e.preventDefault(); 
                  setShowSettingsModal(true); 
                  setDropdownOpen(false); 
                }}
              >
                Ayarlar
              </a>
              {/* GÜNCELLEME: Şifre Güncelle linki artık modal'ı açıyor */}
              <a 
                href="#password" 
                onClick={(e) => { 
                  e.preventDefault(); 
                  setShowPasswordModal(true); 
                  setDropdownOpen(false); 
                }}
              >
                Şifre Güncelle
              </a>
              <a href="#logout" onClick={handleLogout}>Çıkış Yap</a>
            </div>
          </div>
        </div>
      </header>

      {showSettingsModal && (
        <SettingsModal userId={userId} onClose={() => setShowSettingsModal(false)} />
      )}

      {/* YENİ: showPasswordModal true ise, şifre penceresini ekranda göster */}
      {showPasswordModal && (
        <PasswordModal userId={userId} onClose={() => setShowPasswordModal(false)} />
      )}
    </>
  );
};

export default Navbar;
