import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';

// Layouts
import AdminLayout from './components/layout/AdminLayout';

// Pages
import LoginPage from './pages/LoginPage';
import DashboardPage from './pages/admin/DashboardPage';
import InvestmentBanksPage from './pages/admin/InvestmentBanksPage';
import WithdrawalBanksPage from './pages/admin/WithdrawalBanksPage';
import InvestmentRequestsPage from './pages/admin/InvestmentRequestsPage';
import WithdrawalRequestsPage from './pages/admin/WithdrawalRequestsPage';
import UsersPage from './pages/admin/UsersPage';
import SitesPage from './pages/admin/SitesPage';
import ReportsPage from './pages/admin/ReportsPage';
import CustomerYatirimPage from './pages/customer/CustomerYatirimPage'; // Müşteri sayfası importu

function App() {
  const token = localStorage.getItem('authToken');
  const isAuthenticated = !!token; // Token varsa true, yoksa false döner

  return (
    <BrowserRouter>
      <Routes>
        {/* ====================================================== */}
        {/* === MÜŞTERİ TARAFINA AÇIK ROTA (GİRİŞ GEREKTİRMEZ) === */}
        {/* ====================================================== */}
        <Route path="/musteri/yatirim/:apiKey" element={<CustomerYatirimPage />} />


        {/* ====================================================== */}
        {/* === ADMİN PANELİ ROTALARI (GİRİŞ GEREKTİRİR) === */}
        {/* ====================================================== */}

        {/* Login Sayfası Rotası */}
        <Route 
          path="/login" 
          element={!isAuthenticated ? <LoginPage /> : <Navigate to="/dashboard" />} 
        />

        {/* Giriş Yapılmışsa Gösterilecek Admin Sayfaları */}
        <Route 
          path="/" 
          element={isAuthenticated ? <AdminLayout /> : <Navigate to="/login" />}
        >
          {/* Ana dizine gelindiğinde dashboard'a yönlendir */}
          <Route index element={<Navigate to="/dashboard" />} /> 

          {/* Admin paneli alt sayfaları */}
          <Route path="dashboard" element={<DashboardPage />} />
          <Route path="investment-banks" element={<InvestmentBanksPage />} />
          <Route path="withdrawal-banks" element={<WithdrawalBanksPage />} />
          <Route path="investment-requests" element={<InvestmentRequestsPage />} />
          <Route path="withdrawal-requests" element={<WithdrawalRequestsPage />} />
          <Route path="users" element={<UsersPage />} />
          <Route path="sites" element={<SitesPage />} />
          <Route path="reports" element={<ReportsPage />} />
        </Route>

      </Routes>
    </BrowserRouter>
  );
}

export default App;
